 function _rules(){
		$this->form_validation->set_rules('id_agama', 'id_agama', 'trim');
		$this->form_validation->set_rules('agama', 'agama', 'trim|xss_clean|required');
		$this->form_validation->set_error_delimiters('<span class="text-danger">', '</span>');
  }

 function index(){
