  }

 function tambah($status=''){
    if ($status=='aksi') {
        $json = array('success'=>false ,'alert'=>array());
        if ($this->input->is_ajax_request()) {
            $this->_rules();
            if ($this->form_validation->run()) {
                $insert = array(
																'agama' => $this->input->post('agama',TRUE),
																'created_at' => date('Y-m-d h:i:s'),
																'created_by' => sess('id_auth'),
															);
                $this->model->get_insert($insert);
                $json['alert']   = 'Berhasil Menyimpan!';
                $json['success'] = true;
            }else{
                foreach ($_POST as $key => $value) {
                  $json['alert'][$key] = form_error($key);
                }
            }
            echo json_encode($json);
        }
    }else{
      $this->layouts->set_title('Agama');
      $data = array('layout_title' => 'Ref_agama',
                      'button'=>'tambah',
                      'aksi' =>site_url(config_item("cpanel").'ref_agama/tambah/aksi'),
											'id_agama' => set_value('id_agama'),
											'agama' => set_value('agama'),
											);
			 //$this->layouts->view(config_item("cpanel").'content/ref_agama/form',array(),$data);
  //MODAL TAMBAH
  $this->layouts->view(config_item("cpanel").'content/ref_agama/form',array(),$data,false);
     }
  }

 function edit($id,$status=''){
      if ($status=='aksi') {
          $json = array('success'=>false ,'alert'=>array());
          if ($this->input->is_ajax_request()) {
              $this->_rules();
              if ($this->form_validation->run()) {
                  $update = array(
																'agama' => $this->input->post('agama',TRUE),
																'update_at' => date('Y-m-d h:i:s'),
																'update_by' => sess('id_auth'),
															);
                $this->model->get_update($id,$update);
                $json['alert']   = 'Berhasil Mengedit!';
                $json['success'] = true;
            }else{
                foreach ($_POST as $key => $value) {
                  $json['alert'][$key] = form_error($key);
                }
            }
            echo json_encode($json);
        }
    }else{
      if($row=$this->model->get_where($id)){
        $this->layouts->set_title('Agama');
