    }
  }
}

 function hapus($id){
  if ($this->input->is_ajax_request()) {
    if ($this->model->get_delete($id)==false) {
      $data['alert'] = 'Gagal Menghapus, data ini terpakai di table lain!';
    }else {
      $data['alert'] = 'Berhasil Menghapus!';
    }
      echo json_encode($data);
    }
}

