 function tambah($status=''){
    if ($status=='aksi') {
        $json = array('success'=>false ,'alert'=>array());
        if ($this->input->is_ajax_request()) {
            $this->_rules();
            if ($this->form_validation->run()) {
                $insert = array(
																'nama' => $this->input->post('nama',TRUE),
																'telepon' => $this->input->post('telepon',TRUE),
																'alamat' => $this->input->post('alamat',TRUE),
															);
                $this->model->get_insert($insert);
                $json['alert']   = 'Berhasil Menyimpan!';
                $json['success'] = true;
            }else{
                foreach ($_POST as $key => $value) {
                  $json['alert'][$key] = form_error($key);
                }
            }
            echo json_encode($json);
        }
    }else{
      $this->layouts->set_title('Polres/Polsek');
      $data = array('layout_title' => 'Polres/Polsek',
                      'button'=>'tambah',
                      'aksi' =>site_url(config_item("cpanel").'ref_polres/tambah/aksi'),
