																'telepon' => $this->input->post('telepon',TRUE),
																'alamat' => $this->input->post('alamat',TRUE),
															);
                $this->model->get_update($id,$update);
                $json['alert']   = 'Berhasil Mengedit!';
                $json['success'] = true;
            }else{
                foreach ($_POST as $key => $value) {
